mapper.select:
SELECT b.id, b.title, b.inserted, COUNT(DISTINCT r.id) numOfReply, m.nickName writerNickName, COUNT(f.id) hasFile FROM Board b LEFT JOIN Reply r ON b.id = r.board_id JOIN Member m ON b.memberId = m.id LEFT JOIN File f ON b.id = f.boardId
WHERE b.title LIKE #{keyword } OR b.body LIKE #{keyword }
WHERE b.title LIKE #{keyword }
WHERE b.body LIKE #{keyword }
GROUP BY b.id ORDER BY b.id DESC

mapper.update:
UPDATE Board
SET title = #{title},
	body = #{body}
WHERE 
    id = #{id}

mapper.select:
SELECT id
FROM Board
WHERE memberId = #{memberId}

SELECT fileName 
FROM File
WHERE boardId = #{boardId}

SELECT fileName
FROM File
WHERE boardId = #{boardId}

mapper.delete:
DELETE FROM File
WHERE boardId = #{boardId}
  AND fileName = #{fileName}
