mapper.select:
SELECT COUNT(id) 
FROM Member 
WHERE id = #{id}

SELECT COUNT(email)
FROM Member
WHERE email = #{email}

SELECT COUNT(nickName)
FROM Member
WHERE nickName = #{nickName}

mapper.update:
UPDATE Member
SET password = #{password},
    email = #{email},
    nickName = #{nickName}
WHERE
    id = #{id}

UPDATE Member
SET password = #{pw}
WHERE id = #{id}
